import React from 'react'
import { Component, createRef } from 'react'


// react.d.ts
interface RefObject<T> {
    // immutable
    readonly current: T | null
}

// react.d.ts
//function createRef<T>(): RefObject<T>

// Define props for the Music Element Board
type PlayMusicProps = {
    pauseMusic: boolean
}


class PlayMusic extends React.Component<PlayMusicProps> {
    constructor(props: any) {
        super(props)
    }
    private musicRef = createRef<HTMLAudioElement>()
    render() {
        if (!this.props.pauseMusic) {
            return (
            <div>

                    <audio ref={this.musicRef} loop>
                
                <source src="https://archive.org/embed/TetrisThemeMusic" type="audio/mpeg" /> 
                    Your browser does not support the audio element.
                </audio>
            </div>
            )
}    
        }
    
    }
    
export default PlayMusic;
